mapper.insert:
INSERT INTO user_info_tb( USER_ID
                        , USER_PWD
                        , USERNAME
                        , USER_TYPE
                        , USER_TEL
                        , JOIN_DATE)
VALUES ( #{userId}
       , SHA2(#{userPwd}, 256)
       , #{userName}
       , #{userType}
       , #{userTel}
       , NOW())

mapper.select:
SELECT USER_ID
FROM user_info_tb
WHERE USER_ID = #{userId}
