mapper.select:
SELECT DRIVE_REPORT_ID, DATE_FORMAT(DRIVE_DATE, '%m.%d') AS driveDate, COMPANY, FROM_SITE, TO_SITE, ITEM, QUANTITY, UNIT_PRICE FROM join_reports WHERE 1=1
AND GROUP_TITLE = #{title}
AND COMPANY = #{company}
ORDER BY TITLE DESC
ORDER BY COMPANY ASC

mapper.update:
UPDATE group_drive_report_tb
SET GROUP_PAYMENT_CHK = #{paymentBtnFlag}
WHERE GROUP_WRITER_ID_FK = #{groupWriterIdFk}

    AND GROUP_TITLE = #{title}


    AND COMPANY = #{company}

AND GROUP_PAYMENT_CHK = !#{paymentBtnFlag}
