mapper.update:
UPDATE drive_report_tb
SET PROGRESS   = #{progress},
    SUBMIT_CHK = #{submitChk}
WHERE DRIVE_REPORT_ID = #{driveReportId}
  AND DRIVER_ID_FK = #{driverIdFk}
  AND SUBMITTER_PAYMENT_CHK = 0

mapper.delete:
DELETE
FROM drive_report_tb
WHERE DRIVE_REPORT_ID = #{driveReportId}
  AND WRITER_ID_FK = #{writerIdFk}
  AND SUBMITTER_PAYMENT_CHK = 0
