mapper.select:
SELECT DRIVE_ID, DATE_FORMAT(DRIVE_DATE, '%m.%d') AS driveDate, ITEM, PAYMENT_CHK, LAST_KM, USED_AMOUNT, USED_OIL, MEMO, PAYMENT_CHK, SCHD_REPL_KM, IF(SCHD_REPL_DATE IS NULL, '1999-01-01', SCHD_REPL_DATE) AS replDate, FROM mileage_tb WHERE USER_ID_IDX_FK = #{userId_IDX_FK} AND DRIVE_DATE BETWEEN #{startDate} AND #{endDate}
AND ITEM = #{item}
ORDER BY driveDate DESC, ITEM ASC
ORDER BY ITEM ASC, driveDate DESC

mapper.update:
UPDATE mileage_tb
SET PAYMENT_CHK = 1
WHERE USER_ID_IDX_FK = #{userId_IDX_FK}
AND driveDate BETWEEN #{startDate} AND #{endDate}

    AND ITEM = #{item}

AND PAYMENT_CHK = 0

UPDATE mileage_tb
SET PAYMENT_CHK = 0
WHERE USER_ID_IDX_FK = #{userId_IDX_FK}
AND driveDate BETWEEN #{startDate} AND #{endDate}

    AND ITEM = #{item}

AND PAYMENT_CHK = 1
