mapper.select:
SELECT MILEAGE_ID, DATE_FORMAT(DRIVE_DATE, '%m.%d') AS driveDate, ITEM, LAST_KM, USED_AMOUNT, MEMO, REPL_ACTIVE_CHK FROM mileage_tb WHERE USER_ID_IDX_FK = #{userIdIdxFk} AND DRIVE_DATE BETWEEN #{startDate} AND #{endDate}
AND ITEM = #{item}
ORDER BY driveDate DESC, ITEM ASC
ORDER BY ITEM ASC, driveDate DESC

mapper.update:
UPDATE mileage_tb
SET PAYMENT_CHK = 1
WHERE USER_ID_IDX_FK = #{userId_IDX_FK}
AND driveDate BETWEEN #{startDate} AND #{endDate}

    AND ITEM = #{item}

AND PAYMENT_CHK = 0

UPDATE mileage_tb
SET PAYMENT_CHK = 0
WHERE USER_ID_IDX_FK = #{userId_IDX_FK}
AND driveDate BETWEEN #{startDate} AND #{endDate}

    AND ITEM = #{item}

AND PAYMENT_CHK = 1
