mapper.select:
SELECT driveID, drvclub, DATE_FORMAT(drvdate, '%m.%d') AS 'drvdate', chk2, lastkm, useamt, useOil, drvrem, rependchk, IF(rependdate IS NULL, '1999-01-01',rependdate) AS 'rependdate', repaddkm FROM tdrive WHERE carno = #{carNo} AND drvdate BETWEEN #{startDate} AND #{endDate}
AND drvclub = #{selectOption}
ORDER BY drvdate DESC, drvclub ASC
ORDER BY drvclub ASC, drvdate DESC

mapper.update:
UPDATE tdrive
SET chk2 = 1
WHERE carno = #{carNo}
AND drvdate BETWEEN #{startDate} AND #{endDate}

    AND drvclub = #{selectOption}

AND chk2 = 0

UPDATE tdrive
SET chk2 = 0
WHERE carno = #{carNo}
AND drvdate BETWEEN #{startDate} AND #{endDate}

    AND drvclub = #{selectOption}

AND chk2 = 1
