mapper.sql:
WITH covering_tb1 AS (
SELECT MILEAGE_ID
FROM mileage_tb
WHERE WRITER_ID_FK = ${writerIdFk}
AND  DRIVE_DATE >= ${startDate} AND DRIVE_DATE <= ${endDate}

    AND ITEM = ${item}

ORDER BY MILEAGE_ID DESC
)

covering_tb2 AS(
SELECT MILEAGE_ID
FROM covering_tb1
ORDER BY MILEAGE_ID DESC
 LIMIT ${startId},${pageSize} 
)

mapper.update:
UPDATE mileage_tb
SET PAYMENT_CHK = #{paymentBtnFlag}
WHERE WRITER_ID_FK = #{writerIdFk}
AND DRIVE_DATE BETWEEN #{startDate} AND #{endDate}

    AND ITEM = #{item}

AND PAYMENT_CHK = !#{paymentBtnFlag}
