mapper.update:
UPDATE group_drive_report_tb
SET GROUP_DATE            = #{groupDate},
    GROUP_TITLE           = #{groupTitle},
    GROUP_MEMO            = #{groupMemo},
    GROUP_SUBMITTER_ID_FK = #{groupSubmitterIdFk},
    GROUP_PAYMENT_CHK     = #{groupPaymentChk},
    GROUP_SUBMIT_CHK      = #{groupSubmitChk}
WHERE GROUP_WRTIER_ID_FK = #{groupWriterIdFk}
  AND GROUP_ID = #{groupId}
  AND SUBMITTER_PAYMENT_CHK = 0

UPDATE group_drive_report_tb
SET SUBMIT_CHK = #{submitChk}
WHERE GROUP_DRIVER_ID_FK = #{groupDriverIdFk}
  AND GROUP_ID = #{groupId}
  AND GROUP_SUBMITTER_PAYMENT_CHK = 0

mapper.select:
SELECT GROUP_ID, DATE_FORMAT(GROUP_DATE, '%Y.%m.%d') AS groupDate, GROUP_TITLE, GROUP_MEMO, GROUP_SUBMITTER_ID_FK, GROUP_PAYMENT_CHK, GROUP_SUBMIT_CHK FROM group_drive_report_tb WHERE
GROUP_DATE BETWEEN #{groupDate} AND #{groupDate} AND
GROUP_DRIVER_ID_FK = #{groupDriverIdFk}

mapper.delete:
DELETE
FROM group_drive_report_tb
WHERE GROUP_WRITER_ID_FK = #{groupWriterIdFk}
  AND GROUP_ID = #{groupId}
  AND GROUP_SUBMITTER_PAYMENT_CHK = 0

update.foreach:
UPDATE drive_report_tb
SET PROGRESS   = #{progress},
SUBMIT_CHK = #{submitChk}
WHERE DRIVER_ID_FK = #{driveReport.driverIdFk}
AND DRIVE_REPORT_ID = #{driveReport.driveReportId}
AND SUBMITTER_PAYMENT_CHK = 0

mapper.select:
SELECT DRIVE_REPORT_ID, DATE_FORMAT(DRIVE_DATE, '%Y.%m.%d') AS driveDate, FROM_SITE, TO_SITE, ITEM, UNIT_PRICE, QUANTITY, MEMO, PAYMENT_CHK, PROGRESS, SUBMITTER_ID_FK FROM drive_report_tb WHERE
DRIVE_DATE BETWEEN #{driveDate} AND #{driveDate} AND
DRIVER_ID_FK = #{driverIdFk}
